import React, { useState, useEffect } from 'react';
import Button from '../../../../UI/Button/Button';
import Spotify from '../../../../models/Spotify';
import { SEARCH_TYPES } from '../../../../constants';

const LibraryButton = ({ id, inLibrary, libraryType }) => {

    const handleShowFollow = (id) => {
        Spotify.saveUserShow(id);
    };

    const handleShowUnfollow = (id) => {
        Spotify.removeUserShow(id);
    };

    const handleEpisodeLike = (id) => {

    }

    const handleEpisodeRemove = (id) => {

    }

    return (
        <Button
            type="button"
            outline
            onClick={inLibrary ?
                libraryType === SEARCH_TYPES.show ?
                    () => handleShowUnfollow(id)
                    :
                    () => handleEpisodeRemove(id)
                :
                libraryType === SEARCH_TYPES.show ?
                    () => handleShowFollow(id)
                    :
                    () => handleEpisodeLike(id)
            }>
            {inLibrary ?
                libraryType === SEARCH_TYPES.show ? 'Unfollow' : 'Remove'
                :
                libraryType === SEARCH_TYPES.show ? 'Follow' : 'Like'
            }
        </Button>
    );
}

export default LibraryButton;
