import React from 'react';
import styled from 'styled-components';

const Loading = styled.div`
    margin: 0 auto;
    font-size: 10px;
    width: 4em;
    height: 3em;
    border-radius: 50%;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const Dot = styled.span`
    display: block;
    margin: 0.5em;
    width: 1em;
    height: 1em;
    border-radius: 50%;
    background: ${({ theme }) => theme.colors.positive};
    @keyframes bounce {
        100% {
            opacity: 0;
            transform: scale(0.5);
        }
    }
`;

const FirstDot = styled(Dot)`
    order: 1;
    animation: bounce 0.75s 0s infinite ease-in-out alternate;
`;

const SecondDot = styled(Dot)`
    order: 2;
    animation: bounce 0.75s 0.3s infinite ease-in-out alternate;
`;

const ThirdDot = styled(Dot)`
    order: 3;
    animation: bounce 0.75s 0.6s infinite ease-in-out alternate;
`;

function LoadingIcon(props) {
    return (
        <Loading>
            <FirstDot/>
            <SecondDot/>
            <ThirdDot/>
        </Loading>
    );
}

export default LoadingIcon;
