import React, { createContext, useState, useContext, useRef } from 'react';
import { QueueContext } from './QueueContextProvider';

export const PlayerContext = createContext();

const PlayerContextProvider = (props) => {
    const [isPlaying, updateIsPlaying] = useState(false);
    // const [audio, updateAudio] = useState();
    const audioRef = useRef();
    const { currentEpisode, loadQueueNext, loadQueuePrevious } = useContext(QueueContext);

    const startPlaying = async () => {
        if (audioRef && currentEpisode) {
            updateIsPlaying(true);
            const playPromise = audioRef.play();
            if (playPromise !== undefined) {
                await playPromise;
            }
        }
    }

    const stopPlaying = async () => {
        if (audioRef && currentEpisode) {
            updateIsPlaying(false);
            const pausePromise = audioRef.pause();
            if (pausePromise !== undefined) {
                await pausePromise;
            }
        }
    }

    const resetPlayer = () => {
        if (audioRef && currentEpisode) {
            stopPlaying();
            audioRef.currentTime = 0;
            startPlaying();
        }
    }

    const playNext = () => {
        loadQueueNext();
    }

    const playPrevious = () => {
        loadQueuePrevious();
    }

    return (
        <PlayerContext.Provider value={{
            isPlaying,
            // updateAudio,
            startPlaying,
            stopPlaying,
            playNext,
            playPrevious,
            resetPlayer
        }}>
            {props.children}
            <audio src={currentEpisode?.audio_preview_url} ref={audioRef}/>
        </PlayerContext.Provider>
    );
}

export default PlayerContextProvider;
