import React, { useState, createContext } from 'react';

export const LogoutModalContext = createContext();

const LogoutModalContextProvider = (props) => {
    const [modalActive, updateModalActive] = useState(true);
    const [modalContent, updateModalContent] = useState();

    const openModal = (content) => {
        updateModalContent(content);
        updateModalActive(true);
    }

    const closeModal = () => {
        updateModalContent();
        updateModalActive(false);
    }

    return (
        <LogoutModalContext.Provider value={{
            modalActive,
            modalContent,
            openModal,
            closeModal
        }}>
            {props.children}
        </LogoutModalContext.Provider>
    );
}

export default LogoutModalContextProvider;
