export const msToHours = (ms) => {
    const minutes = Math.floor(Number(ms) / 1000 / 60);
    const hours = Math.floor(minutes / 60);

    console.log(hours, minutes);

    let result = '';

    if (hours === 1) {
        result += `${hours} hour`;
    } else if (hours > 1) {
        result += `${hours} hours`;
    }

    return result;
}
