import React, { Fragment } from 'react';
import ShowEpisode from './ShowEpisode/ShowEpisode';
import styled from 'styled-components';

const Titles = styled.header`
    display: none;

    @media (min-width: 1380px) {
        display: grid;
        grid-template-rows: auto;
        grid-template-columns: 1fr 15fr 3fr 3fr 3fr;
        gap: 1em;

        & > * {
            margin-top: 0;
            align-self: start;
        }
    }
`;

const ShowEpisodes = ({ episodes }) => {
    return (
        <Fragment>
            <Titles>
                <div>Preview</div>
                <div>Title</div>
                <div>Date</div>
                <div>Duration</div>
                <div>Link</div>
            </Titles>
            { episodes ? episodes.items.map(episode => {
                return <ShowEpisode
                    key={episode.id}
                    name={episode.name}
                    description={episode.description}
                    releaseDate={episode.release_date}
                    duration={episode.duration_ms}
                    source={episode.external_urls.spotify}
                    preview={episode.audio_preview_url} />}) : null }
        </Fragment>
    );
}

export default ShowEpisodes;
