import React, { useState, useEffect } from 'react';
import Button from '../../../../UI/Button/Button';
import Spotify from '../../../../models/Spotify';
import { SEARCH_TYPES } from '../../../../constants';

const LibraryButton = ({ id, inLibrary, libraryType }) => {
    let text;
    let action;

    const handleShowFollow = (id) => {
        Spotify.saveUserShow(id);
    };

    const handleShowUnfollow = (id) => {
        Spotify.removeUserShow(id);
    };

    const handleEpisodeLike = (id) => {

    }

    const handleEpisodeRemove = (id) => {

    }

    useEffect(() => {
        if(inLibrary && libraryType === SEARCH_TYPES.show) {
            text = 'Unfollow';
            action = () => handleShowUnfollow(id);
        } else if(inLibrary && libraryType === SEARCH_TYPES.episode) {
            text = 'Remove';
            action = () => handleEpisodeRemove(id);
        } else if(!inLibrary && libraryType === SEARCH_TYPES.show) {
            text = 'Follow';
            action = () => handleShowFollow(id);
        } else if(!inLibrary && libraryType === SEARCH_TYPES.episode) {
            text = 'Like';
            action = () => handleEpisodeLike(id);
        }
    }, []);

    return (
        <Button type="button" onClick={action} outline>
            {text}
        </Button>
    );
}

export default LibraryButton;
