import React from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';

const Element = styled.button`
    background: none;
    border: none;
    outline: none;

    svg {
        transform: scale(${({ scale }) => scale ? scale : 1});
        transition: transform .2s ease-in-out;
    }

    &:hover {
        cursor: pointer;

        svg {
            transform: scale(1.1);
        }
    }
`;

function StopButton(props) {
    return (
        <Element type="button" {...props}>
            <svg xmlns="http://www.w3.org/2000/svg" width="26" height="26" viewBox="0 0 40 40">
                <g transform="translate(-45 -389)">
                    <path id="Icon_material-pause" dataName="Icon material-pause" d="M9,21.963h4.132V7.5H9ZM17.265,7.5V21.963H21.4V7.5Z" transform="translate(49.699 393.677)" fill="#fff"/>
                    <g id="Ellipse_2" dataName="Ellipse 2" transform="translate(45 389)" fill="none" stroke="#fff" strokeWidth="1">
                        <circle cx="20" cy="20" r="20" stroke="none"/>
                        <circle cx="20" cy="20" r="19.5" fill="none"/>
                    </g>
                </g>
            </svg>
        </Element>
    );
}

StopButton.propTypes = {
    scale: PropTypes.number
}

export default StopButton;
