import React, { useState, useEffect, Fragment } from 'react';
import { withRouter } from 'react-router-dom';
import { SEARCH_TYPES } from '../../../../constants';
import Spotify from '../../../../models/Spotify';
import WorkspaceLoading from '../../WorkspaceLoading/WorkspaceLoading';
import Details from '../../Details/Details';
import ShowEpisodes from './ShowEpisodes/ShowEpisodes';

const ShowDetails = ({ location }) => {
    const [isLoading, updateIsLoading] = useState(true);
    const [details, updateDetails] = useState({});

    useEffect(() => {
        (async () => {
            const path = location.pathname;
            const showId = path.substring(path.lastIndexOf('/') + 1);
            try {
                const {
                    name,
                    publisher,
                    external_urls,
                    description,
                    images,
                    episodes } = await Spotify.getDetails(showId, SEARCH_TYPES.show);


                console.log('XD');



                updateDetails({
                    name,
                    publisher,
                    external: external_urls.spotify,
                    description,
                    image: images[1].url,
                    episodes
                });

                updateIsLoading(false);
            } catch(err) {
                console.error(err);
            }
        })();
    }, [location.pathname]);

    return (
        <Fragment>
            <Details {...details} type={SEARCH_TYPES.show} />
            <ShowEpisodes episodes={details.episodes}/>
            <WorkspaceLoading loading={isLoading.toString()}/>
        </Fragment>
    );
}

export default withRouter(ShowDetails);
