import React, { createContext, useState, useContext } from 'react';
import { QueueContext } from './QueueContextProvider';

export const PlayerContext = createContext();

const PlayerContextProvider = (props) => {
    const [isPlaying, updateIsPlaying] = useState(false);
    const { currentEpisode, loadQueueNext, loadQueuePrevious } = useContext(QueueContext);

    const startPlaying = (audio) => {
        if (audio) {
            audio.play();
            updateIsPlaying(true);
        }
    }

    const stopPlaying = (audio) => {
        if (audio) {
            audio.pause();
            updateIsPlaying(false);
        }
    }

    // audio && currentEpisode
    const resetPlayer = (audio) => {
        console.log(audio, currentEpisode);
        if (audio) {
            stopPlaying(audio);
            audio.currentTime = 0;
            startPlaying(audio);
        }
    }

    const playNext = () => {
        loadQueueNext();
    }

    const playPrevious = () => {
        loadQueuePrevious();
    }

    return (
        <PlayerContext.Provider value={{
            isPlaying,
            startPlaying,
            stopPlaying,
            resetPlayer,
            playNext,
            playPrevious
        }}>
            {props.children}
        </PlayerContext.Provider>
    );
}

export default PlayerContextProvider;
