import React, { useContext, Fragment } from 'react';
import styled from 'styled-components';
import { ModalContext } from '../../contexts/ModalContextProvider';
import Button from '../../UI/Button/Button';
import Auth from '../../controllers/Auth';

const Confirmation = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Paragraph = styled.p`
    margin-bottom: 1em;
`;

const ButtonConfirm = styled(Button)`
    margin-right: 0.5em;
`;


const Backdrop = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0.75);
    z-index: 900;
    opacity: ${({ active }) => active ? '1' : '0'};
    visibility: ${({ active }) => active ? 'visible' : 'hidden'};
    transition: opacity 0.2s ease-in-out,
                visibility 0.2s ease-in-out;
`;

const Container = styled.div`
    position: absolute;
    display: inline-block;
    text-align: center;
    width: 90%;
    top: 50%;
    left: 50%;
    padding: 2em;
    transform: translate(-50%, -50%);
    background-color: ${({ theme }) => theme.colors.primary};
    border-radius: 1em;
    z-index: 1000;
    opacity: ${({ active }) => active ? '1' : '0'};
    visibility: ${({ active }) => active ? 'visible' : 'hidden'};
    transition: opacity 0.2s ease-in-out,
                visibility 0.2s ease-in-out;

    @media (min-width: 500px) {
        width: auto;
    }
`;

const Modal = (props) => {
    const { modalActive, closeModal } = useContext(ModalContext);

    return (
        <Fragment>
            <Backdrop active={modalActive} onClick={closeModal}/>
            <Container active={modalActive}>
                <Confirmation>
                <Paragraph>Are you sure you want to log out?</Paragraph>
                <div>
                    <ButtonConfirm type="button" onClick={Auth.logOut}>Yes</ButtonConfirm>
                    <Button type="button" outline onClick={closeModal}>No</Button>
                </div>
                </Confirmation>
            </Container>
        </Fragment>
    );
}

export default Modal;
