import React, { useState, useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import { SEARCH_TYPES } from '../../../constants/searchTypes';
import Spotify from '../../../models/Spotify';

const Details = ({ location }) => {

    const [details, updateDetails] = useState({});

    useEffect(() => {
        (async () => {
            const searchParams = new URLSearchParams(location.search);
            const id = searchParams.get('id');
            const type = searchParams.get('type');

            if(type === SEARCH_TYPES.show) {
                const showDetails = await Spotify.getShowDetails(id);
                updateDetails(showDetails);
                return;
            }

            if (type === SEARCH_TYPES.episode) {
                const episodeDetails = await Spotify.getEpisodeDetails(id);
                updateDetails(episodeDetails)
                return;
            }
        })();
    }, [])

    return (
    <div>{details.name} {details.description}</div>
    );
}

export default withRouter(Details);
