import React from 'react';
import styled from 'styled-components';
import PlayButton from '../../../UI/PlayButton/PlayButton';
import PauseButton from '../../../UI/PauseButton/PauseButton';
import SkipButton from '../../../UI/SkipButton/SkipButton';

const Element = styled.div`
    position: fixed;
    bottom: 0;
    left: 0;
    z-index: 700;
    width: 100%;
    background-color: ${({ theme }) => theme.colors.tertiary};
    @media (min-width: 1024px) {
        z-index: 1000;
    }
`;

const Progress = styled.div`
    width: 100%;
    height: 5px;
    background-color: #414141;
`;

const Content = styled.div`
    display: flex;
    padding: 2em;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Bar = styled.div`
    // width: *dynamic*
    height: 100%;
    background-color: ${({ theme }) => theme.colors.specific};


    width: 20%
`;

const Controls = styled.div`
    margin-top: 1em;
    display: flex;
    align-items: center;
`;

const MiddleButton = styled.div`
    margin: 0 1em;
    display: inline-block;
`;

const Name = styled.p`
    font-size: ${({ theme }) => theme.typography.small};
`;

const Player = (props) => {
    const isPlaying = false;

    return (
        <Element>
            <Progress>
                <Bar/>
            </Progress>
            <Content>
                <Controls>
                    <SkipButton direction="backward" scale={1.15}/>
                    <MiddleButton>
                        { isPlaying ?
                            <PauseButton scale={1.15}/>
                            :
                            <PlayButton scale={1.15}/>
                        }
                    </MiddleButton>
                    <SkipButton direction="forward" scale={1.15}/>
                </Controls>
                <Name>
                    SPI 437: Life After Being a High-Performance CEO with Jon Oringer
                </Name>
            </Content>
        </Element>
    );
}

export default Player;
