import React from 'react';
import styled from 'styled-components';
import Accordion from '../../../../../../UI/Accordion/Accordion';

const Element = styled.div`
    margin-top: 1em;
    padding-top: 1em;
    border-top: 1px solid #505050;
`;

const Lead = styled.h3`
    font-size: inherit;
    font-weight: 400;
`;

const Description = styled(Accordion)`
    color: ${({ theme }) => theme.colors.specific};
    max-width: 100%;
    margin: 0.5em 0;
`;

const Date = styled.time`
    color: ${({ theme }) => theme.colors.specific};
`;

const Link = styled.a`
    display: block;
    color: inherit;
`;

const ShowEpisode = ({ name, description, releaseDate, duration, source }) => {
    return (
        <Element>
            <Lead>{name}</Lead>
            <Description>{description}</Description>
            <Date dateTime={releaseDate}>{releaseDate}</Date> â€¢ {duration}
            <Link href={source}>Listen on Spotify</Link>
        </Element>
    );
}

export default ShowEpisode;
