import React, { useContext, Fragment } from 'react';
import styled from 'styled-components';
import { ModalContext } from '../../contexts/ModalContextProvider';

const Backdrop = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0.75);
    z-index: 9998;
    opacity: ${props => props.active ? '1' : '0'};
    visibility: ${props => props.active ? 'visible' : 'hidden'};
    transition: opacity 0.2s ease-in-out,
                visibility 0.2s ease-in-out;
`;

const Content = styled.div`
    position: absolute;
    padding: 3em;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background-color: var(--primary);
    border-radius: 1em;
    z-index: 9999;
    opacity: ${props => props.active ? '1' : '0'};
    visibility: ${props => props.active ? 'visible' : 'hidden'};
    transition: opacity 0.2s ease-in-out,
                visibility 0.2s ease-in-out;
`;

const Close = styled.button`

`;

const Modal = (props) => {
    const { modalActive, modalContent, closeModal } = useContext(ModalContext);

    return (
        <Fragment>
            <Backdrop active={modalActive} onClick={closeModal}/>
            <Content active={modalActive}>
                { modalContent }
                <Close onClick={closeModal}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 29.657 29.657">
                    <g id="X" transform="translate(2.828 2.828)">
                    <line id="Line_3" data-name="Line 3" x1="24" y2="24" stroke-width="4" stroke="#000" stroke-linecap="round" stroke-linejoin="round" fill="none"/>
                    <line id="Line_4" data-name="Line 4" x2="24" y2="24" stroke-width="4" stroke="#000" stroke-linecap="round" stroke-linejoin="round" fill="none"/>
                    </g>
                    </svg>
                </Close>
            </Content>
        </Fragment>
    );
}

export default Modal;
