import React from 'react';
import styled from 'styled-components';

const Loading = styled.div`
    font-size: 10px;
    width: 4em;
    height: 3em;
    border-radius: 50%;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;

&:before,
&:after,
span {
    content: '';
    width: 0.8em;
    height: 0.8em;
    margin: 0.2em;
    border-radius: 50%;
    background: #fff;
    display: block;
}
&:before {
    order: 1;
    animation: demo20-loading-anim 1s 0s infinite ease-in-out alternate;
}
&:after {
    order: 2;
    animation: demo20-loading-anim 1s 0.3s infinite ease-in-out alternate;
}
span {
    order: 3;
    animation: demo20-loading-anim 1s 0.6s infinite ease-in-out alternate;
}
@keyframes demo20-loading-anim {
    100% {
        opacity: 0;
        transform: scale(0.5);
    }
}
`;

function LoadingIcon(props) {
    return (
        <Loading>
            <span></span>
        </Loading>
    );
}

export default LoadingIcon;
