import React, { useState, useEffect, Fragment } from 'react';
import styled from 'styled-components';

const Description = styled.p`
    color: ${({ theme }) => theme.colors.specific};
`;

const ShowDetailsContent = ({ name, description, image, episodes }) => {

    const [episodesPreviews, updateEpisodesPreviews] = useState(null);

    useEffect(() => {
        if(episodes) {
            const audioPreviews = episodes.items.map(episode => {
                return <audio key={episode.id} src={episode.audio_preview_url} />
            });
            updateEpisodesPreviews(audioPreviews);
        }
    }, [episodes]);

    return (
        <Fragment>
            <header>
                <img src={image} alt="" />
                <h2>{name}</h2>
                <Description>{description}</Description>
            </header>
            <div>
                {episodesPreviews}
            </div>
        </Fragment>
    );
}

export default ShowDetailsContent;
