import React, { useState, useEffect } from 'react';
import Button from '../../../../UI/Button/Button';
import Spotify from '../../../../models/Spotify';
import { SEARCH_TYPES } from '../../../../constants';

const LibraryButton = ({ type, id }) => {
    const [text, updateText] = useState('');

    useEffect(() => {
        (async () => {
            if(id) {
                const inLibrary = await Spotify.checkUserShow(id);
                if(inLibrary && type === SEARCH_TYPES.show) {
                    updateText('Unfollow');
                    return;
                }

                if(inLibrary && type === SEARCH_TYPES.episode) {
                    updateText('Remove');
                    return;
                }

                if(!inLibrary && type === SEARCH_TYPES.show) {
                    updateText('Follow');
                    return;
                }

                if(!inLibrary && type === SEARCH_TYPES.episode) {
                    updateText('Like');
                    return;
                }
            }
        })();
    }, [id])

    const handleShowFollow = (id) => {
        Spotify.saveUserShow(id);
    };

    const handleShowUnfollow = (id) => {
        Spotify.removeUserShow(id);
    };

    return (
        <Button type="button" outline>
            {text}
        </Button>
    );
}

export default LibraryButton;
