import React from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';

const Element = styled.button`
    padding: 0.5em;
    background: none;
    border: none;
    outline: none;

    svg {
        transform: scale(${({ scale }) => scale ? scale : 1});
        transition: transform .2s ease-in-out;
    }

    &:hover {
        cursor: pointer;

        svg {
            transform: scale(${({ scale }) => scale ? scale * 1.1 : 1.1});
        }
    }
`;

function SkipButton(props) {
    return (
        <Element type="button" {...props}>
            <svg xmlns="http://www.w3.org/2000/svg" width="8.299" height="11.492" viewBox="0 0 8.299 11.492">
            <path id="Path_2" data-name="Path 2" d="M12.8,4.5V15.992L4.5,10.246Z" transform="translate(-4.5 -4.5)" fill="#FFF"/>
            </svg>
        </Element>
    );
}

SkipButton.propTypes = {
    direction: PropTypes.oneOf(['backward', 'forward'])
}

export default SkipButton;
