class Spotify {

    static retrieveToken() {
        return JSON.parse(localStorage.getItem('access'));
    }

    static async getSearchResults(query) {
        const token = this.retrieveToken();
        console.log(token);

        if (!token) {
            throw new Error('Not token provided');
        }

        try {
            const data = await fetch(`https://api.spotify.com/v1/search?q=${query}&type=show,episode&limit=50`, {
                headers: {
                    'Authorization': 'Bearer ' + token
                }
            });
            return await data.json();
        } catch (err) {
            console.error(err);
        }
    }

    static async getShowDetails(id) {
        const token = this.retrieveToken();

        if (!token) {
            return;
        }

        try {
            const data = await fetch(`https://api.spotify.com/v1/shows/${id}`, {
                headers: {
                    'Authorization': 'Bearer ' + token
                }
            });
            return await data.json();
        } catch (err) {
            console.error(err);
        }
    }

    static async getUserProfile() {
        const token = this.retrieveToken();

        if (!token) {
            return;
        }

        try {
            const data = await fetch(`https://api.spotify.com/v1/me`, {
                headers: {
                    'Authorization': 'Bearer ' + token
                }
            });
            const results = await data.json();
            return results;
        } catch (err) {
            console.error(err);
        }
    }
}

export default Spotify;
