import React, { createContext, useState, useEffect } from 'react';

export const PlayerContext = createContext();

const PlayerContextProvider = (props) => {
    const [queue, updateQueue] = useState([]);
    const [queueHead, updateQueueHead] = useState(0);
    const [currentEpisode, updateCurrentEpisode] = useState();

    const loadQueue = (currentId, newQueue) => {
      const currentIndex = newQueue.findIndex(episode => episode.id === currentId) || 0;
      updateQueue(newQueue);
      updateQueueHead(currentIndex);
      updateCurrentEpisode(newQueue[currentIndex]);
    }

    const unshiftQueueHead = () => {
      updateCurrentEpisode(queue[queueHead - 1]);
      updateQueueHead(prev => prev - 1);
    }

    const shiftQueueHead = () => {
      updateCurrentEpisode(queue[queueHead + 1]);
      updateQueueHead(prev => prev + 1);
    }

    return (
        <PlayerContext.Provider value={{
            currentEpisode,
            queueHead,
            loadQueue,
            unshiftQueueHead,
            shiftQueueHead
        }}>
            {props.children}
        </PlayerContext.Provider>
    );
}

export default PlayerContextProvider;
