import React, { useState, useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import { SEARCH_TYPES } from '../../../../constants';
import Spotify from '../../../../models/Spotify';
import ShowDetailsContent from './ShowDetailsContent/ShowDetailsContent';

const ShowDetails = ({ location }) => {
    const [details, updateDetails] = useState({});

    useEffect(() => {
        (async () => {
            const path = location.pathname;
            const showId = path.substring(path.lastIndexOf('/') + 1);

            const {
                name,
                description,
                images,
                episodes } = await Spotify.getDetails(showId, SEARCH_TYPES.show);

            const details = await Spotify.getDetails(showId, SEARCH_TYPES.show);

            updateDetails({
                name,
                description,
                image: images[1].url,
                episodes
            });
        })();
    }, []);

    return <ShowDetailsContent {...details} />;
}

export default withRouter(ShowDetails);
