import React from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';

const StyledButton = styled.button`
    width: ${props => props.fluid === true ? '100%' : 'auto'};
    padding: 0.5em 2em;
    background-color: ${props => props.outline === true ? 'transparent' : 'var(--accent)'};
    border: ${props => props.outline === true ? '2px solid var(--accent)' : 'none'};
    border-radius: 100px;
    box-sizing: border-box;
    outline: none;
    font-family: inherit;
    font-size: var(--small);
    letter-spacing: 1px;
    color: var(--light);
    transition: background-color 0.1s ease-in-out,
                border 0.1s ease-in-out;
    cursor: pointer;

    &:hover {
        background-color: ${props => props.outline === true ? 'auto' : 'var(--attention)'};
        border-color: ${props => props.outline === true ? 'var(--attention)' : 'auto'};
    }
`;

function Button(props) {
    return <StyledButton type={props.type} fluid={props.fluid} outline={props.outline} className={props.className}>{props.children}</StyledButton>;
}

Button.propTypes = {
    type: PropTypes.oneOf(['button', 'submit']),
    fluid: PropTypes.bool,
    outline: PropTypes.bool
}

export default Button;
