import React, { useState, useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import { SEARCH_TYPES } from '../../../../constants';
import Spotify from '../../../../models/Spotify';
import ShowDetailsHeader from './ShowDetailsHeader/ShowDetailsHeader';

const ShowDetails = ({ location }) => {
    const [details, updateDetails] = useState({});

    useEffect(() => {
        (async () => {
            const path = location.pathname;
            const id = path.substring(path.lastIndexOf('/') + 1);
            const { name, description, images, episodes } = await Spotify.getDetails(id, SEARCH_TYPES.show);
            updateDetails({
                name,
                description,
                image: images[1],
                episodes
            });
        })();
    }, []);

    return (
        <div>
           <ShowDetailsHeader {...details} />
        </div>
    );
}

export default withRouter(ShowDetails);
