import React, { Fragment } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import PropTypes from 'prop-types';
import { SEARCH_TYPES } from '../../../constants';
import capitalizeString from '../../../utils/capitalizeString';
import Accordion from '../../../UI/Accordion/Accordion';
import Button from '../../../UI/Button/Button';
import convertTime from '../../../utils/convertTime';


const Image = styled.img`
    display: block;
    max-width: 250px;
    object-fit: contain;
    object-position: top;
`;

const Header = styled.header``;

const Lead = styled.h2`
    position: relative;
    font-size: 2em;
    line-height: 1.25;
    color: ${({ theme }) => theme.colors.positive};

    @media (min-width: 500px) {
        max-width: calc(0.5em * 50);
    }
`;

const Label = styled.p`
    font-size: ${({ theme }) => theme.typography.small};
    font-weight: 400;
    color: ${({ theme }) => theme.colors.specific};
`;

const Type = styled(Label)`
    margin: 0.75em 0 0.25em 0;

    @media (min-width: 1024px) {
        margin: 0 0 0.25em 0;
    }
`;

const Publisher = styled(Label)`
    margin: 0.25em 0 0.75em 0;
`;

const Show = styled(Label)`
    margin: 0.25em 0 0.75em 0;
`;

const InternalLink = styled(Link)`
    text-decoration: none;
    color: inherit;

    &:hover {
        text-decoration: underline;
    }
`;

const Description = styled.p`
    color: ${({ theme }) => theme.colors.specific};
`;

const Controls = styled.div`
    margin-top: 0.75em;
`;

const PlayButton = styled(Button)`
    margin-left: 0.75em;
`;

const Links = styled.div`
    margin-top: 0.75em;
    @media (min-width: 500px) {
        display: inline-block;
        margin-top: 0;
        margin-left: 0.75em;
    }
`;

const ExternalLink = styled.a`
    display: inline-block;
    text-decoration: none;
    color: ${({ theme }) => theme.colors.specific};

    &:hover {
        text-decoration: underline;
    }
`;

const Split = styled.div`
    @media (min-width: 1024px) {
        display: flex;
    }
`;

const Content = styled.div`
    @media (min-width: 1024px) {
        margin-left: ${({ type }) => type === SEARCH_TYPES.show ? '3em' : '0'};
    }
`;

const Date = styled.time`
    display: inline-block;
`;

const Duration = styled.div`
    position: relative;
    display: inline-block;
    margin-left: 1em;

    &::before {
        content: '•';
        position: absolute;
        top: 50%;
        left: -0.5em;
        transform: translate(-50%, -50%);
    }
`;

const Details = ({ payload }) => {
    return (
        <Fragment>
            <Split>
                <Image src={payload.type === SEARCH_TYPES.show ? payload.image : null} alt=""/>
                <Content type={payload.type}>
                    <Header>
                        <Type>
                            {payload.type ? capitalizeString(payload.type) : null}
                        </Type>
                        <Lead>
                            {payload.name}
                        </Lead>
                        {payload.type === SEARCH_TYPES.show ?
                            <Publisher>
                                By {payload.publisher}
                            </Publisher>
                            :
                            <Show>
                                <InternalLink to={`/shows/${payload.showId}`}>
                                    {payload.showName}
                                </InternalLink>
                            </Show>
                        }
                    </Header>
                    {payload.type === SEARCH_TYPES.show ?
                        <Accordion>
                            {payload.description}
                        </Accordion>
                        :
                        <Description>
                            {payload.description}
                        </Description>
                    }
                    <Controls>
                        <Button type="button">
                            {payload.type === SEARCH_TYPES.show ?
                                'Follow'
                                :
                                'Like'
                            }
                        </Button>
                        <PlayButton type="button" outline>
                            Play
                        </PlayButton>
                        <Links>
                            <ExternalLink href={payload.external}>
                                Listen on Spotify
                            </ExternalLink>
                        </Links>
                    </Controls>
                    {payload.type === SEARCH_TYPES.episode ?
                        <div>
                            <Date dateTime={payload.releaseDate}>
                                {payload.releaseDate}
                            </Date>
                            <Duration>{convertTime(payload.duration)}</Duration>
                        </div>
                        :
                        null
                    }
                </Content>
            </Split>
        </Fragment>
    );
}

Details.propTypes = {
    type: PropTypes.oneOf([SEARCH_TYPES.show, SEARCH_TYPES.episode])
}

export default Details;
