import React, { createContext, useState } from 'react';

export const MeasuresContext = createContext();

const MeasuresContextProvider = (props) => {
    const [measures, updateMeasures] = useState({});

    return (
        <MeasuresContext.Provider value={{
            measures,
            updateMeasures
        }}>
            {props.children}
        </MeasuresContext.Provider>
    );
}

export default MeasuresContextProvider;
